UPDATE `common_notification_email_template` SET `template_name` = 'VCARD_PAYMENT_SUCCESS', `subject` = 'Payment Redemption', `template_data` = '<html>\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n</head>\r\n<body>\r\n<table style=\"color: #207580;\">\r\n    <tr>\r\n        <td style=\"font-weight: bold\">Dear <span id=\"EMAIL_SEND_TO\">name of recipient</span>,</td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>We are pleased to inform you that \'<span id=\"CUSTOMER_NAME\">customer name</span>\' has successfully created a payment for the following bill/s using a \'Virtual Card\'</td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>\r\n            <table style=\"border-collapse: collapse; color: #207580;\">\r\n                <tr id=\"TRANSACTION_DETAILS_HEADER\">\r\n                    <th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Bill No</th>\r\n                    <th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Created Date</th>\r\n                    <th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Reference No</th>\r\n                    <th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: right; height: 25px; padding: 6px;\">Amount</th>\r\n                </tr>\r\n                <tr style=\"border-top: 1px #207580 solid\">\r\n                    <td></td>\r\n                    <td></td>\r\n                    <td></td>\r\n                    <td id=\"total\" style=\"text-align: right; font-weight: bold;\"><span id=\"TOTAL_AMOUNT\">0.00</span></td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>Please find below <b>credentials</b> to authorize the virtual card:</td>\r\n    </tr>\r\n    <tr>\r\n        <td>\r\n        <table style=\"border-collapse: collapse; color: #207580; margin-top: 10px;\">\r\n            <tr>\r\n                <td style=\"font-weight: bold\">Credential</td>\r\n                <td style=\"font-weight: bold\">:</td>\r\n                <td style=\"padding-left: 10px;\"><span id=\"CREDENTIALS\">Credentials</span></td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"font-weight: bold\">Payment Number</td>\r\n                <td style=\"font-weight: bold\">:</td>\r\n                <td style=\"padding-left: 10px;\"><span id=\"PAYMENT_REF\">Payment reference</span></td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"font-weight: bold\">Payee</td>\r\n                <td style=\"font-weight: bold\">:</td>\r\n                <td style=\"padding-left: 10px;\"><span id=\"PAYEE\">Supplier Name</span></td>\r\n            </tr>\r\n        </table>\r\n        </td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td>The virtual card provided is intended for single-time use only. Thus, once the card has been utilized for a transaction, it cannot be used again for any subsequent transactions. The payment is available to redeem on <span id=\"AVAILABLE_FROM\">06/15/2023</span> and will expire in 30 days on <span id=\"AVAILABLE_TO\">06/15/2023</span>.</td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td><span style=\"font-weight: bold;\"><ul></ul><ul></ul><ul></ul><ul></ul>Thank You!</span></td>\r\n    </tr>\r\n    <tr>\r\n        <td><img style=\"width: 170px; height: 45px;margin-top: 25px;\" src=\"https://papertrl.com/wp-content/uploads/2019/09/logo.png\"/></td>\r\n    </tr>\r\n    <tr>\r\n        <td>This email is an automated notification, Replies to this message will not be read or responded to. We\'re\r\n            happy to help you with any questions or concerns you may have. Please contact us directly 24/7 via <span\r\n                    style=\"font-weight: bold\">support@papertrl.com</span>\r\n            <p class=\"MsoNormal\"><span style=\"font-size:8.0pt;color:#207580\">CONFIDENTIALITY NOTICE: This e-mail transmission, and any attachments, is intended only for the use of PaperTrl Inc. or recipient named above and may contain information that\r\n                            is confidential, privileged and exempt from disclosure under applicable law. If you are not the intended recipient or authorized to receive this message for the recipient, you must not use, copy, disclose or take any action based on this message or any information\r\n                            herein. If you have received this message in error, please advise the sender immediately.</span><span\r\n                    style=\"font-size:8.0pt;color:#014708\"></span></p>\r\n            <p><span style=\"font-size:18.0pt;font-family:Webdings;color:green\">P</span><span style=\"color:black\">\r\n                        </span><span style=\"font-size:10.0pt;font-family:&quot;Helvetica&quot;,sans-serif;color:green\">Be Environmentally Responsible. Please don\'t print this e-mail unless you really need to.</span><u></u><u></u>\r\n            </p>\r\n        </td>\r\n    </tr>\r\n</table>\r\n</body>\r\n</html>\r\n', `event_id` = NULL, `status` = 'A', `created_by` = 'admin', `created_on` = '2023-02-09', `update_by` = NULL, `update_on` = NULL, `delete_by` = NULL, `delete_on` = NULL WHERE `id` = 39;
