UPDATE `common_notification_email_template` SET `template_data` = '<html>\n<head>\n<meta charset=\"UTF-8\">\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n</head>\n<body>\n<table style=\"border-collapse: collapse; color: #207580;\">\n<tr>\n<td style=\"font-weight: bold; color: #207580;\">Dear <span id=\"EMAIL_SEND_TO\">name of recipient</span>,</td>\n</tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td>A payment issued to you for the following bill/s has been cancelled by the \'<span id=\"CUSTOMER_NAME\">customer name</span>\'.</td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr>\n<td>\n<table style=\"border-collapse: collapse; color: #207580;\">\n<tr id=\"TRANSACTION_DETAILS_HEADER\" style=\"color: #207580;\">\n<th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Bill No</th>\n<th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Created Date</th>\n<th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Canceled Date</th>\n<th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: left; height: 25px; padding: 6px;\">Reference No</th>\n<th style=\"border-bottom: 1px #207580 solid; width: 140px; text-align: right; height: 25px; padding: 6px;\">Amount</th>\n</tr>\n<tr style=\"border-top: 1px #207580 solid\">\n<td></td>\n<td></td>\n<td></td>\n<td></td>\n<td style=\"text-align: right; font-weight: bold\"><span id=\"TOTAL_AMOUNT\">0.00</span></td>\n</tr>\n</table>\n</td>\n</tr>\n<tr><td></td></tr>\n<tr>\n<td>The reason for cancellation is \'<span id=\"CANCEL_REASON\">payment type</span>\'. Please contact your customer for more information.</td>\n</tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr><td></td></tr>\n<tr style=\"margin-top: 65px\">\n<td style=\"font-weight: bold\">\n<ul></ul>\n<ul></ul>\n<ul></ul>\n<ul></ul>\nThank You!\n</td>\n</tr>\n<tr>\n<td>\n<img style=\"width: 170px;height: 45px;\" src=\"https://papertrl.com/wp-content/uploads/2019/09/logo.png\"/>\n</td>\n</tr>\n<tr>\n<td>This email is an automated notification, Replies to this message will not be read or responded to. We\'re happy to help you with any questions or concerns you may have. Please contact us directly 24/7 via <span style=\"font-weight: bold\">support@papertrl.com</span>\n<p style=\"font-size:8.0pt;color:#207580;\">CONFIDENTIALITY NOTICE: This e-mail transmission, and any attachments, is intended only for the use of PaperTrl Inc. or recipient named above and may contain information that is confidential, privileged and exempt from disclosure under applicable law. If you are not the intended recipient or authorized to receive this message for the recipient, you must not use, copy, disclose or take any action based on this message or any information herein. If you have received this message in error, please advise the sender immediately.</p>\n<p style=\"color:black; font-size:10.0pt;font-family:&quot;Helvetica&quot;,sans-serif;color:green;\">Be Environmentally Responsible. Please don\'t print this e-mail unless you really need to.</p>\n</td>\n</tr>\n</table>\n</body>\n</html>' WHERE `id` = 43;