UPDATE `common_notification_email_template` SET `template_name` = 'PAYMENT_ERROR_NOTIFY_SUPPORT_TEAM', `subject` = 'Payment Error', `template_data` = '<html>\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n</head>\r\n<body>\r\n<table style=\"color: #207580;\">\r\n    <tr>\r\n        <td style=\"font-weight: bold; color: #207580;\">Dear team,</td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>A payment has failed due to a technical issue with the system. Transaction details are as below</td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>\r\n            <table style=\"border-collapse: collapse; color: #207580; margin-top: 10px; margin-bottom: 10px;\">\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Bill No/s</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"BILL_NO\">Bill nos</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Vendor</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"VENDOR\">Vendor</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Tenant Id</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"TENANT_ID\">Tenant Id</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Date and Time</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"DATE_N_TIME\">Date and time</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Payment Type</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"PAYMENT_TYPE\">Payment type</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td style=\"font-weight: bold; text-align: left;\">Failed Reason</td>\r\n                    <td style=\"font-weight: bold; text-align: left;\">:</td>\r\n                    <td style=\"text-align: left; padding-left: 10px;\"><span id=\"FAILED_REASON\">Failed reason</span></td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>Please investigate and resolve the matter as soon as possible.</td>\r\n    </tr>\r\n    <tr><td></td></tr>\r\n    <tr><td></td></tr>\r\n    <tr>\r\n        <td>\r\n            <span style=\"font-weight: bold;\"><ul></ul><ul></ul><ul></ul><ul></ul>Thank You!</span>\r\n        </td>\r\n    </tr>\r\n    <tr>\r\n        <td>\r\n            <img style=\"width: 170px;height: 45px;margin-top: 25px;\" src=\"https://papertrl.com/wp-content/uploads/2019/09/logo.png\"/>\r\n        </td>\r\n    </tr>\r\n    <tr>\r\n        <td>This email is an automated notification, Replies to this message will not be read or responded to. We\'re\r\n            happy to help you with any questions or concerns you may have. Please contact us directly 24/7 via <span\r\n                    style=\"font-weight: bold\">support@papertrl.com</span>\r\n            <p style=\"font-size:8.0pt;color:#207580;\">CONFIDENTIALITY NOTICE: This e-mail transmission, and any attachments, is intended only for the use of PaperTrl Inc. or recipient named above and may contain information that\r\n                            is confidential, privileged and exempt from disclosure under applicable law. If you are not the intended recipient or authorized to receive this message for the recipient, you must not use, copy, disclose or take any action based on this message or any information\r\n                            herein. If you have received this message in error, please advise the sender immediately.</p>\r\n            <p style=\"font-size:10.0pt;font-family:&quot;Helvetica&quot;,sans-serif;color:green;\">Be Environmentally Responsible. Please don\'t print this e-mail unless you really need to.</p>\r\n        </td>\r\n    </tr>\r\n</table>\r\n</body>\r\n</html>\r\n', `event_id` = NULL, `status` = 'A', `created_by` = 'admin', `created_on` = '2023-04-07', `update_by` = NULL, `update_on` = NULL, `delete_by` = NULL, `delete_on` = NULL WHERE `id` = 42;
